function Drumpad(props){

    const handleClick = (e) =>{
        console.log(props.drum.label);
        playClip();
    }


    const playClip = () => {
        document.getElementById(props.drum.id).play();
    }


    return(
        <a className="drum-pad" id={props.drum.label} onClick={handleClick}>
            <audio src={props.drum.src} id={props.drum.id} className="clip"></audio>
            {props.drum.id}
        </a>
    );
}

export default Drumpad;
