import React, { useState } from 'react';
import Display from './components/display';
import Drumpad from './components/drumpad';
import './App.css';

// font imports

import "./fonts/Ledsim-JRz7o.ttf";

function App() {

  const db_url = "./audio/";

  function initAudios() {
    const promises = drumObj.drums.map(async drum => {
      const resp = await fetch(db_url + drum.src) // fetch the file
        ;
      const buf = await resp.arrayBuffer();
      const AudioBuf = await drumObj.audio_ctx.decodeAudioData(buf);
      drum.buf = AudioBuf; // store the audioBuffer (won't change)
      return await Promise.resolve(drum);
    });
    return Promise.all(promises); // when all are loaded
  }

  const drumObj = {
    audio_ctx: new AudioContext(),
    generate_sound: (part) => {
      const source = drumObj.audio_ctx.createBufferSource();
      source.buffer = drum.buf;
      source.connect(drumObj.gain); 
      drum.source = source;
      source.start(0);
    },   
    drums: [{
        pad: 1,
        id: "Q",
        keycode: 81,
        label: "High Tom",
        src: "/audio/hitom.wav"
      },
      {
        pad: 2,
        id: "W",
        keycode: 87,
        label: "Mid Tom",
        src: "/audio/midtom2.wav"
      },
      {
        pad: 3,
        id: "E",
        keycode: 69,
        label: "Low Tom",
        src: "/audio/loetom.wav"
      },
      {
        pad: 4,
        id: "A",
        keycode: 65,
        label: "Kick",
        src: "/audio/808bd2.wav"
      },
      {
        pad: 5,
        id: "S",
        keycode: 83,
        label: "Open High Hat",
        src: "/audio/707-ohh.wav"
      },
      {
        pad: 6,
        id: "D",
        keycode: 68,
        label: "Foot High Hat ",
        src: "/audio/big-foot-hh.wav"
      },
      {
        pad: 7,
        id: "Z",
        keycode: 90,
        label: "Snare",
        src: "/audio/brass-sd2.wav"
      },
      {
        pad: 8,
        id: "X",
        keycode: 88,
        label: "Bright Open High Hat",
        src: "/audio/909-bright-ohh.wav"
      },
      {
        pad: 9,
        id: "C",
        keycode: 67,
        label: "Collapsed Hat",
        src: "/audio/909-collapsed-hat.wav"
      }

    ],  
    record: function record(e) {
      const recordBtn = document.getElementById('record');
      const chunks = [];
      const recorder = new MediaRecorder(drumObj.streamNode.stream);

      recorder.ondataavailable = e => chunks.push(e.data);
      recorder.onstop = e => {

        // export our recording to a new <audio> element
        const blob = new Blob(chunks);
        const url = URL.createObjectURL(blob);
        const a = new Audio(url);
        a.controls = true; // enable audio controls for new <audio> element
        document.getElementById('records').appendChild(a);

        // reset default click handler
        recordBtn.onclick = drumObj.record;
        // add styling to record button to indicate start recording

      }
      recordBtn.onclick = function () {
        recorder.stop();
      };
      // start recording
      recorder.start();
      // add styling to record button to indicate stop recording
    }
  }; // end drumObj block

  drumObj.gain = drumObj.audio_ctx.createGain();
  drumObj.gain.gain.value = 1;
  drumObj.gain.connect(drumObj.audio_ctx.destination);

  // for recording
  drumObj.streamNode = drumObj.audio_ctx.createMediaStreamDestination();
  drumObj.gain.connect(drumObj.streamNode);

  document.getElementById('record').onclick = drumObj.record;

  const [display, setDisplay] = useState("");
  const [volume, setVolume] = useState(1);

  const handleVolumeControl = (e) => {
    setVolume(e.target.value);
  }

  return (
    <div className="App">
      <div className="App-header">React Drum Machine</div>
      <div id="drum-machine">
        <div id="drumpad-container">
          {drumObj.drums.map((drum) => <Drumpad key={drum.id} drum={drum} displayHandler={setDisplay} volume={volume}/>)}
        </div>
        <Display value={display} volume={volume} handleChange={handleVolumeControl}/>
      </div>
    </div>
  );
}

export default App;
