import React from "react";


function Display(props){




    const controlButtons = {
        record: {
            label: "record",
            imgSrc: "/images/round-record.png"
        },
        stop: {
            label: "stop",
            imgSrc: "/images/round-stop.png"
        },
        play: {
            label: "play",
            imgSrc: "/images/round-play.png"
        },
        pause: {
            label: "pause",
            imgSrc: "/images/round-pause.png"
        },
        metronome: {
            label: "metronome",
            imgSrc: "/images/round-blank.png"
        },
        left: {
            label: "left",
            imgSrc: "/images/round-back.png"
        },
        right: {
            label: "right",
            imgSrc: "/images/round-forward.png"
        },
    }


    return (
        <div id="display-container">
            <div id="display">
                <div className="line">
                    <span>VOLUME</span>
                    <span>{Math.round(props.volume * 100)}%</span>
                </div>
                <div className="line">
                    <span>DRUM</span>
                    <span>{props.value.toUpperCase()}</span>
                </div>
                <div className="line">
                    <span>RECORDING</span>
                    <span>{props.rec ? "ON" : "OFF"}</span>
                </div>
                <div className="line">
                    <span>TRACK</span>
                    <span>{props.track}/{props.records ? props.records.length:0}</span>
                </div>
            </div>
            <input 
                id="vol-control" 
                type="range" 
                min="0" max="1" 
                value={props.volume} 
                onChange={props.handleChange}
                step=".01"/>
            <div id="records" />
            <div id="audio-buttons">
                <div id="record" onClick={props.handleRecorder}><img src={controlButtons.record.imgSrc} alt=""/></div>
                <div id="play" onClick={()=>{props.play(props.records[props.track-1])}}><img src={props.trackPlaying ? controlButtons.pause.imgSrc : controlButtons.play.imgSrc} alt=""/></div>
                <div id="back" onClick={() => {props.setTrack(props.track - 1)}}><img src={controlButtons.left.imgSrc} alt=""/></div>
                <div id="forward" onClick={() => {props.setTrack(props.track + 1)}}><img src={controlButtons.right.imgSrc} alt=""/></div>

                <div id="metronome" ><img src={controlButtons.metronome.imgSrc} alt=""/></div>                
            </div> 
            <div id="records"></div>
        </div>
    );
}

export default Display;
