import React, { useState } from "react";

function Drumpad({ drum, displayHandler, volume }){

    const [active, setActive] = useState(false);
    const activeStyle = {"backgroundColor": "red"};

    React.useEffect(() => {
        document.addEventListener("keydown", handleKeypress);
        return () => {
            document.removeEventListener("keydown", handleKeypress);
        };
    }, []); 

    const handleKeypress = (e) => {
        e.keyCode === drum.keycode && playClip();
    };




    const playClip = () => {
        setActive(true);
        setTimeout(setActive(false), 2000);
        const sound = document.getElementById(drum.id);
        sound.currentTime = 0;
        sound.volume = volume;
        sound.play();
        displayHandler(drum.label);

    };


    return(
        <div className="drum-pad" id={drum.label} onClick={playClip}>
            {drum.id}
            <audio src={drum.src} id={drum.id} className="clip"></audio>
            <div className={`pad $(active && "pad-active")`} id={drum.pad} style={ active ? activeStyle:{}}/>
            {/*<div className="row">
                <span>PAD {drum.pad}</span>
                <span>KEY {drum.id}</span>
    </div>*/}

        </div>
    );
}

export default Drumpad;
