import React from "react";


function Display(props){
    return (
        <div id="display-container">
            <div id="display">
                <div className="line">
                    <div>VOLUME</div>
                    <div>{props.volume * 100}%</div>
                </div>
                <div className="line">
                    <div>DRUM HIT</div>
                    <div>{props.value.toUpperCase()}</div>
                </div>
                <div className="line">{props.value.toUpperCase()}</div>
            </div>
        </div>
    );
}

export default Display;
