import React, { useState } from "react";


function Display(props){

    const [track, setTrack] = useState(props.records.length > 0 ? 1 : 0);


    const controlButtons = {
        record: {
            label: "record",
            imgSrc: "/images/round-record.png"
        },
        stop: {
            label: "stop",
            imgSrc: "/images/round-stop.png"
        },
        play: {
            label: "play",
            imgSrc: "/images/round-play.png"
        },
        pause: {
            label: "pause",
            imgSrc: "/images/round-pause.png"
        },
        metronome: {
            label: "metronome",
            imgSrc: "/images/round-blank.png"
        },
    }


    return (
        <div id="display-container">
            <div id="display">
                <div className="line">
                    <span>VOLUME</span>
                    <span>{Math.round(props.volume * 100)}%</span>
                </div>
                <div className="line">
                    <span>DRUM</span>
                    <span>{props.value.toUpperCase()}</span>
                </div>
                <div className="line">
                    <span>RECORDING</span>
                    <span>{props.rec ? "ON" : "OFF"}</span>
                </div>
                <div className="line">
                    <span>TRACK</span>
                    <span>{track}/{props.records ? props.records.length:0}</span>
                </div>
            </div>
            <input 
                id="vol-control" 
                type="range" 
                min="0" max="1" 
                value={props.volume} 
                onChange={props.handleChange}
                step=".01"/>
            <div id="records" />
            <div id="audio-buttons">
                <div id="record" onClick={props.handleRecorder}><img src={controlButtons.record.imgSrc} alt=""/></div>
                <div id="play" ><img src={controlButtons.play.imgSrc} alt=""/></div>
                <div id="pause" ><img src={controlButtons.pause.imgSrc} alt=""/></div>
                <div id="stop" ><img src={controlButtons.stop.imgSrc} alt=""/></div>

                <div id="metronome" ><img src={controlButtons.metronome.imgSrc} alt=""/></div>                
            </div> 
            <div id="records"></div>
        </div>
    );
}

export default Display;
