import React from "react";


function Display(props){

    const controlButtons = [
        {
            label: "record",
            imgSrc: "/images/"
        },
        {
            label: "stop",
            imgSrc: "/images/"
        },
        {
            label: "play",
            imgSrc: "/images/"
        },
        {
            label: "clear",
            imgSrc: "/images/"
        },
        {
            label: "metronome",
            imgSrc: "/images/"
        },
    ]

    return (
        <div id="display-container">
            <div id="display">
                <div className="line">
                    <span>VOLUME</span>
                    <span>{props.volume * 100}%</span>
                </div>
                <div className="line">
                    <span>DRUM</span>
                    <span>{props.value.toUpperCase()}</span>
                </div>
                <div className="line">
                    <span>RECORDING</span>
                    <span>OFF</span>
                </div>
            </div>
            <input 
                id="vol-control" 
                type="range" 
                min="0" max="1" 
                value={props.volume} 
                onChange={props.handleChange}
                step=".01"/>
            <div id="audio-buttons">
                <div id="record" />
                <div id="pause" />
                <div id="stop" />
                <div id="play" />                
            </div>    
        </div>
    );
}

export default Display;
