import React from "react";


function Display(props){

    /*
    const controlButtons = {
        record: {
            label: "record",
            imgSrc: "/images/round-record.png"
        },
        stop: {
            label: "stop",
            imgSrc: "/images/round-stop.png"
        },
        play: {
            label: "play",
            imgSrc: "/images/round-play.png"
        },
        pause: {
            label: "pause",
            imgSrc: "/images/round-pause.png"
        },
        metronome: {
            label: "metronome",
            imgSrc: "/images/round-blank.png"
        },
    }

    */
    return (
        <div id="display-container">
            <div id="display">
                <div className="line">
                    <span>VOLUME</span>
                    <span>{Math.round(props.volume * 100)}%</span>
                </div>
                <div className="line">
                    <span>DRUM</span>
                    <span>{props.value.toUpperCase()}</span>
                </div>
                <div className="line">
                    <span>RECORDING</span>
                    <span>OFF</span>
                </div>
            </div>
            <input 
                id="vol-control" 
                type="range" 
                min="0" max="1" 
                value={props.volume} 
                onChange={props.handleChange}
                step=".01"/>
            {/*<div id="records" />
            <div id="audio-buttons">
