import React from 'react';
import Display from './components/display';
import Drumpad from './components/drumpad';
import './App.css';

function App() {
  const drumArray = {
    drumOne: {
      id: "Q",
      label: "High Tom",
      src: "/audio/39[kb]hitom.wav.mp3"
    },
    drumTwo: {
      id: "W",
      label: "Mid Tom",
      src: "/audio/44[kb]midtom2.wav.mp3"
    },
    drumThree: {
      id: "E",
      label: "Low Tom",
      src: "/audio/90[kb]loetom.wav.mp3"
    },
    drumFour: {
      id: "A",
      label: "Kick",
      src: "/audio/120[kb]basic-ekick.wav.mp3"
    },
    drumFive: {
      id: "S",
      label: "Open High Hat",
      src: "/audio/18[kb]909-lofi-ohh.wav.mp3"
    },
    drumSix: {
      id: "D",
      label: "Foot High Hat ",
      src: "/src/audio/13[kb]big-foot-hh.wav.mp3"
    },
    drumSeven: {
      id: "Z",
      label: "Snare",
      src: "/audio/155[kb]brass-sd2.wav.mp3"
    },
    drumEight: {
      id: "X",
      label: "Bright Open High Hat",
      src: "/audio/21[kb]brightohh808.wav.mp3"
    },
    drumNine: {
      id: "C",
      label: "Cymbal",
      src: "/audio/82[kb]opencym.wav.map3"
    }

  }

  return (
    <div className="App">
      <div className="App-header">React Drum Machine</div>
      <div id="drum-machine">
        <div id="drumpad-container">
          <Drumpad drum={drumArray.drumOne}/>
          <Drumpad drum={drumArray.drumTwo}/>
          <Drumpad drum={drumArray.drumThree}/>
          <Drumpad drum={drumArray.drumFour}/>
          <Drumpad drum={drumArray.drumFive}/>
          <Drumpad drum={drumArray.drumSix}/>
          <Drumpad drum={drumArray.drumSeven}/>
          <Drumpad drum={drumArray.drumEight}/>
          <Drumpad drum={drumArray.drumNine}/>
        </div>
        <Display />
      </div>
    </div>
  );
}

export default App;
