import React from "react";


function Display(props){




    const controlButtons = {
        record: {
            label: "record",
            imgSrc: "/images/round-record.png"
        },
        stop: {
            label: "stop",
            imgSrc: "/images/round-stop.png"
        },
        play: {
            label: "play",
            imgSrc: "/images/round-play.png"
        },
        pause: {
            label: "pause",
            imgSrc: "/images/round-pause.png"
        },
        download: {
            label: "download",
            imgSrc: "/images/round-blank.png"
        },
        left: {
            label: "left",
            imgSrc: "/images/round-back.png"
        },
        right: {
            label: "right",
            imgSrc: "/images/round-forward.png"
        },
    }

    const handleTrack = (e) => {
        console.log(e);
        switch (e.currentTarget.id){
            case "back":
                if (props.track <= 1) break;    
                props.setTrack(props.track - 1);
                break;
            case "forward":
                if (props.track <= 1 && props.track.length === props.track) break;
                if (props.track === props.records.length){
                    props.setTrack(1);
                    break;
                }else{
                props.setTrack(props.track + 1);
                break;
                }
            default:
                break;
        }

    };
/*
    const handleDownload = () => {
        console.log(props.records[props.track - 1]);
    };
  */  
    return (
        <div id="display-container">
            <div id="display">
                <div className="line">
                    <span>VOLUME</span>
                    <span>{Math.round(props.volume * 100)}%</span>
                </div>
                <div className="line">
                    <span>DRUM</span>
                    <span>{props.value.toUpperCase()}</span>
                </div>
                <div className="line">
                    <span>RECORDING</span>
                    <span>{props.rec ? "ON" : "OFF"}</span>
                </div>
                <div className="line">
                    <span>TRACK</span>
                    <span>{props.track}/{props.records ? props.records.length:0}</span>
                </div>
            </div>
            <input 
                id="vol-control" 
                type="range" 
                min="0" max="1" 
                value={props.volume} 
                onChange={props.handleChange}
                step=".01"/>
            <div id="records" />
            <div id="audio-buttons">
                <a id="record" href=" " onClick={props.handleRecorder}><img src={controlButtons.record.imgSrc} alt=""/></a>
                <a id="play" href=" " onClick={()=>{props.play(props.records[props.track-1])}}><img src={props.trackPlaying ? controlButtons.pause.imgSrc : controlButtons.play.imgSrc} alt=""/></a>
                <a id="back" href=" " onClick={handleTrack}><img src={controlButtons.left.imgSrc} alt=""/></a>
                <a id="forward" href=" " onClick={handleTrack}><img src={controlButtons.right.imgSrc} alt=""/></a>
                <a id="download" href={props.records.length > 0 ? props.records[props.track - 1].src : " "} download="audio.wav"><img src={controlButtons.download.imgSrc} alt=""/></a>
            </div> 
            <div id="records"></div>
        </div>
    );
}

export default Display;
