import React, { useState } from 'react';
import Display from './components/display';
import Drumpad from './components/drumpad';
import './App.css';

function App() {
  const drumArray = [
    {
      id: "Q",
      keycode: 81,
      label: "High Tom",
      //src: "/audio/hitom.wav"
      src: "https://s3.amazonaws.com/freecodecamp/drums/Heater-1.mp3"
    },
    {
      id: "W",
      keycode: 87,
      label: "Mid Tom",
      src: "/audio/midtom2.wav"
    },
    {
      id: "E",
      keycode: 69,
      label: "Low Tom",
      src: "/audio/loetom.wav"
    },
    {
      id: "A",
      keycode: 65,
      label: "Kick",
      src: "/audio/808bd2.wav"
    },
    {
      id: "S",
      keycode: 83,
      label: "Open High Hat",
      src: "/audio/707-ohh.wav"
    },
    {
      id: "D",
      keycode: 68,
      label: "Foot High Hat ",
      src: "/audio/big-foot-hh.wav"
    },
    {
      id: "Z",
      keycode: 90,
      label: "Snare",
      src: "/audio/brass-sd2.wav"
    },
    {
      id: "X",
      keycode: 88,
      label: "Bright Open High Hat",
      src: "/audio/909-bright-ohh.wav"
    },
    {
      id: "C",
      keycode: 67,
      label: "Collapsed Hat",
      src: "/audio/909-collapsed-hat.wav"
    }

  ];

  const [display, setDisplay] = useState("default");

  React.useEffect(() => {
    document.addEventListener("keydown", handleKeypress);
    return () => {
        document.removeEventListener("keydown", handleKeypress);
    };
}, []); 

const handleKeypress = (e) => {
    e.keyCode === drum.keycode && playClip();
};

  return (
    <div className="App">
      <div className="App-header">React Drum Machine</div>
      <div id="drum-machine">
        <div id="drumpad-container">
          {drumArray.map((drum) => <Drumpad key={drum.id} drum={drum} displayHandler={setDisplay}/>)}
        </div>
        <Display value={display}/>
      </div>
    </div>
  );
}

export default App;
