import React from "react";

function Drumpad({ drum, displayHandler }){

    React.useEffect(() => {
        document.addEventListener("keydown", handleKeypress);
        return () => {
            document.removeEventListener("keydown", handleKeypress);
        };
    }, []); 

    const handleKeypress = (e) => {
        e.keyCode === drum.keycode && playClip();
    };


    const playClip = () => {
        sound = document.getElementById(drum.id);
        sound.play();
        displayHandler(drum.label);
    };


    return(
        <div className="drum-pad" id={drum.label} onClick={playClip}>
            <audio src={drum.src} id={drum.id} className="clip"></audio>
            {drum.id}
        </div>
    );
}

export default Drumpad;
