import React from "react";


function Display(props){
    return (
        <div id="display-container">
            <div id="display">
                <div className="line">
                    <span>VOLUME</span>
                    <span>{props.volume * 100}%</span>
                </div>
                <div className="line">
                    <span>DRUM</span>
                    <span>{props.value.toUpperCase()}</span>
                </div>
                <div className="line">
                    <span>RECORDING</span>
                    <span>OFF</span>
                </div>
            </div>
            <input 
                id="vol-control" 
                type="range" 
                min="0" max="1" 
                value={props.volume} 
                onChange={props.handleChange}
                step=".01"/>
        </div>
    );
}

export default Display;
